catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   //Type appropriate comment here, and begin script below

	var ga = new GlideAjax('IaaSListDatastoreNames');
   ga.addParam('sysparm_name','process');
   ga.addParam('sysparm_datastore_name',g_form.getValue('volume_name'));
   ga.getXML(ListCatalogTypesParse);


   function ListCatalogTypesParse(response) 	
   {	  	   
       var answer = response.responseXML.documentElement.getAttribute("answer");
		answer = JSON.parse(answer);
		//alert(answer);
		g_form.clearOptions('datastore_name');

		for( var i=0 ; i < answer.length ; i++)		   		     
			g_form.addOption('datastore_name',answer[i],answer[i]);

   }

}
