function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below

   var ga = new GlideAjax('ListNetworks');
   ga.addParam('sysparm_name','process');
   ga.addParam('sysparm_services',g_form.getValue('services'));
   ga.addParam('sysparm_project',g_form.getValue('project'));
   ga.getXML(ListNetworksParse);


   function ListNetworksParse(response) 
   {	  
      var answer = response.responseXML.documentElement.getAttribute("answer");
/*	   
      alert(answer);	   
      answer = JSON.parse(answer);
      for( var i=0 ; i < answer.length ; i++)
      {  
	    g_form.addOption('regions',answer[i],answer[i]);		   
      }
*/     //alert(answer);
	   //g_form.addInfoMessage(answer); //JSON String

	   //alert(answer);
	   answer = JSON.parse(answer);
	    //alert(answer);
	   //alert(answer[0]);
	   //alert(answer[0].zone);
	   //alert(answer[1].network);
	   g_form.clearOptions('network');
	   for( var i=0 ; i < answer.length ; i++)
		   {
			    //g_form.clearOptions('services');
			   //g_form.addOption('regions',answer[i].zone,answer[i].zone);
			   //g_form.addOption('regions','A','A');
			   //g_form.addOption('project_networks',answer[i].network,answer[i].network);  
			   g_form.addOption('network',answer[i],answer[i]);
           }
   }

}
