sys_script_include.script:
var DeleteVM = Class.create();
DeleteVM.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	process : function()
	{
		var state = this.getParameter('sysparm_state');
		var name = this.getParameter('sysparm_name');
		if (state == "terminated")
			{
				var r = new sn_ws.RESTMessageV2('x_228286_onesphere.vCenter', 'Default GET');
				var response = r.execute();
				var responseBody = response.getBody();
				var httpStatus = response.getStatusCode();
				var body = JSON.parse(responseBody);
				gs.info("Authentication token:" + body.value);

				r = new sn_ws.RESTMessageV2('x_228286_onesphere.vCenter', 'Get VM ');
				r.setStringParameterNoEscape('vm_name', name);
				response = r.execute();
				responseBody = response.getBody();
				httpStatus = response.getStatusCode();
				body = JSON.parse(responseBody);
				gs.info('JSON.stringify : ' + JSON.stringify(body));
				vm_id = body['value'][0].vm;
				gs.info(vm_id);

				r = new sn_ws.RESTMessageV2('x_228286_onesphere.vCenter', 'Delete VM');
				r.setStringParameterNoEscape('vm', vm_id);
				response = r.execute();
				responseBody = response.getBody();
                httpStatus = response.getStatusCode();
				gs.info(httpStatus);
				return ("Success");


			}

	},
    type: 'DeleteVM'
});
