var IaaSListEsxiHostNames = Class.create();
IaaSListEsxiHostNames.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	process : function()
	{
		var volume_name = this.getParameter('sysparm_esxi_host');
		var array = [];		
		var count = 0;	
		var target = new GlideRecord('x_228286_onesphere_storage');
		target.addQuery('volume_name', volume_name);
		target.query(); // Issue the query to the database to get all records 
		while (target.next()) { 
			// add code here to process the incident record 
			//gs.info(target.datastore_name);	
			var ip_lists  = target.esxi_host;
			ip_lists = '' + ip_lists + '';
			array[count] = ip_lists;
			count = count + 1;					
		}
		gs.info("Inside ListStorageTier" + JSON.stringify(array));
		return JSON.stringify(array);			
	},

    type: 'IaaSListEsxiHostNames'
});
