function findProjectUri(authToken,selectedProject)
{
    try {	
	var projectsUri = new sn_ws.RESTMessageV2();
    projectsUri.setHttpMethod('get');
    projectsUri.setEndpoint(oneSphereUri+'/rest/projects?userQuery=' +selectedProject);
    projectsUri.setRequestHeader("Content-Type","application/json");
    projectsUri.setRequestHeader("Authorization", authToken);
    var projectsUriGetResponse = projectsUri.execute();
    var projectsUriResponseBody = projectsUriGetResponse.getBody();
    var response = JSON.parse(projectsUriResponseBody);     
    gs.info("Project URI" + response.members[0].uri);	
	return response.members[0].uri;
	} catch(ex) {
	   var message = projectsUriGetResponse.getErrorMessage();
       gs.error("Error while finding Project URI: " +message);
	}
    
}
function findVirtualMachineProfileUri(authToken,selectedVmProfile,zoneUri,serviceUri)
{
    try {
	    var vmProfilesRequest = new sn_ws.RESTMessageV2();
		vmProfilesRequest.setEndpoint(oneSphereUri+'/rest/virtual-machine-profiles?query=zoneUri+EQ+' +zoneUri+ 'AND' +'serviceUri+EQ+' +serviceUri);
		vmProfilesRequest.setHttpMethod('get');
		vmProfilesRequest.setRequestHeader("Content-Type","application/json");
		vmProfilesRequest.setRequestHeader("Authorization", authToken);
		var vmProfilesRequestGetResponse = vmProfilesRequest.execute();
		var vmProfilesRequestResponseBody = vmProfilesRequestGetResponse.getBody();
		var profiles = JSON.parse(vmProfilesRequestResponseBody);
				
		var key;
		var array = [];
		var i = 0;
		var flag = 0;
		
	    for (key in profiles.members) 
	    {
		     array[i] = profiles.members[key].name;
			 if (array[i].toString() == selectedVmProfile.toString())
			 {
			    flag = 1; 
			    vmProfileUri = profiles.members[key].uri;
                gs.info("VM Profile UI" +vmProfileUri);
			 }
			 i++;
			 if ( flag == 1)
			 break;
	    }
		 
		 
	} catch(x) {
	  var message = vmProfilesRequestGetResponse.getErrorMessage();
      gs.error("Error while finding Virtual machine Profile URI: " +message);
	
	}
    return vmProfileUri;
}     

function findNetworkUri(authToken, selectedNetwork,zoneUri,projectUri)
{
    try {
	    var networksRequest = new sn_ws.RESTMessageV2();
		networksRequest.setEndpoint(oneSphereUri+'/rest/networks?query=zoneUri+EQ+' +zoneUri+ 'AND' +'projectUri+EQ+' +projectUri);
		networksRequest.setHttpMethod('get');
		networksRequest.setRequestHeader("Content-Type","application/json");
		networksRequest.setRequestHeader("Authorization", authToken);
		var networksRequestGetResponse = networksRequest.execute();
		var networksRequestResponseBody = networksRequestGetResponse.getBody();
		var networksList = JSON.parse(networksRequestResponseBody);
		
		
		var key;
		var array = [];
		var i = 0;
		//var networkUri = null;
		var flag = 0;
		
	    for (key in networksList.members) 
	    {
		    array[i] = networksList.members[key].name;
			gs.info(array[i]);
			if (array[i].toString() == selectedNetwork.toString())
			 {
			    flag = 1; 
			    networksUri = networksList.members[key].uri;
                gs.info("Network URI" +networksUri);
			 }
			 i++;
			 if ( flag == 1)
			 break; 
			
	    }
	     
	
	} catch(x) {
	  var message = networksRequestGetResponse.getErrorMessage();
      gs.error("Error while finding Network URI: " +message);	
	}
	
	return networksUri;

}

function updateVMDetails(autheToken,zoneUri,deploymentUri)

{     	
		
		var zoneRequest = new sn_ws.RESTMessageV2();
		zoneRequest.setEndpoint(oneSphereUri+zoneUri);
		zoneRequest.setHttpMethod('get');
		zoneRequest.setRequestHeader("Content-Type","application/json");
		zoneRequest.setRequestHeader("Authorization", authToken);
		var zoneRequestGetResponse = zoneRequest.execute();
		var zoneRequestResponseBody = zoneRequestGetResponse.getBody();
		var zones = JSON.parse(zoneRequestResponseBody);
		providerUri = zones.providerUri;
		
		var providerNameRequest = new sn_ws.RESTMessageV2();
		providerNameRequest.setEndpoint(oneSphereUri+providerUri+'?view=full');
		providerNameRequest.setHttpMethod('get');
		providerNameRequest.setRequestHeader("Content-Type","application/json");
		providerNameRequest.setRequestHeader("Authorization", authToken);
		var providerNameRequestGetResponse = providerNameRequest.execute();
		var providerNameRequestResponseBody = providerNameRequestGetResponse.getBody();
		var providers = JSON.parse(providerNameRequestResponseBody);
		gs.info("Provider name" + ' ' +providers.name);
		gs.info("Provider Type" + ' ' +providers.providerType.name);
		
		provider = providers.name;
		providerType = providers.providerType.name;

		var vmUpdateRequest = new sn_ws.RESTMessageV2();
		vmUpdateRequest.setEndpoint(oneSphereUri+deploymentUri+'?view=full');
		vmUpdateRequest.setHttpMethod('get');
		vmUpdateRequest.setRequestHeader("Content-Type","application/json");
		vmUpdateRequest.setRequestHeader("Authorization", authToken);
		var vmUpdateRequestGetResponse = vmUpdateRequest.execute();
		var vmUpdateRequestResponseBody = vmUpdateRequestGetResponse.getBody();
		var vmDetails = JSON.parse(vmUpdateRequestResponseBody);
	
        name = 	vmDetails.name;          
        status = vmDetails.state;   
	    gs.info("VM Status" + ' ' +status);
       zone = vmDetails.zone.name;
region = vmDetails.region.name;
serviceType = vmDetails.serviceType.name;
virtualMachineProfile = vmDetails.virtualMachineProfile.name;
memorySizeGB = vmDetails.memorySizeGB + '  ' +'GB';
cpuCount = vmDetails.cpuCount;
diskSizeGB = vmDetails.diskSizeGB + ' ' +'GB';
created = vmDetails.created;
	
	
var userRequest = new sn_ws.RESTMessageV2();
//userRequest.setEndpoint(SnowUri+'/api/now/table/x_228286_onesphere_onesphere_project');
userRequest.setEndpoint(SnowUri+'/api/now/table/x_228286_onesphere_soibam_test');
userRequest.setHttpMethod('post');
userRequest.setBasicAuth(SnowUser,SnowPassword);
userRequest.setRequestHeader("Content-Type","application/json");
 vmRequestBody = '{"created_on":\"'+created+'\","name":\"'+name+'\", "state":\"'+status+'\","zone":\"'+zone+'\", "region":\"'+region+'\","service_type":\"'+serviceType+'\", "virtual_machine_profile":\"'+virtualMachineProfile+'\","memory":\"'+memorySizeGB+'\", "vcpus":\"'+cpuCount+'\","storage":\"'+diskSizeGB+'\","provider":\"'+provider+'\","provider_type":\"'+providerType+'\", "service":\"'+selectedVMFavour+'\"}';	

userRequest.setRequestBody(vmRequestBody);
userGetResponse = userRequest.execute();
gs.info(userGetResponse.getBody());
var responseBody = userGetResponse.getBody();
var json = JSON.parse(responseBody);
gs.info(userGetResponse.getBody());
gs.info(json.result.sys_id);
	
return json.result.sys_id;	

}



function checkDeploymentEndPoints(authToken,deploymentUri)
{
        var vmUpdateRequest = new sn_ws.RESTMessageV2();
		vmUpdateRequest.setEndpoint(oneSphereUri+deploymentUri+'?view=full');
		vmUpdateRequest.setHttpMethod('get');
		vmUpdateRequest.setRequestHeader("Content-Type","application/json");
		vmUpdateRequest.setRequestHeader("Authorization", authToken);
		var vmUpdateRequestGetResponse = vmUpdateRequest.execute();
		var vmUpdateRequestResponseBody = vmUpdateRequestGetResponse.getBody();
		var vmDetails = JSON.parse(vmUpdateRequestResponseBody);
		return vmDetails;
}

function updateNetworkDetails(authToken,deploymentUri,sysId)
{
   
    var vmUpdateRequest = new sn_ws.RESTMessageV2();
	vmUpdateRequest.setEndpoint(oneSphereUri+deploymentUri+'?view=full');
	vmUpdateRequest.setHttpMethod('get');
	vmUpdateRequest.setRequestHeader("Content-Type","application/json");
	vmUpdateRequest.setRequestHeader("Authorization", authToken);
	var vmUpdateRequestGetResponse = vmUpdateRequest.execute();
	var vmUpdateRequestResponseBody = vmUpdateRequestGetResponse.getBody();
	var vmDetails = JSON.parse(vmUpdateRequestResponseBody);
	networkName = vmDetails.deploymentEndpoints[0].name;
	ipAddress = vmDetails.deploymentEndpoints[0].address;
        status = vmDetails.state;
		
    var userRequest = new sn_ws.RESTMessageV2();
	//userRequest.setEndpoint(SnowUri+'/api/now/table/x_228286_onesphere_onesphere_project/'+sysId);
	userRequest.setEndpoint(SnowUri+'/api/now/table/x_228286_onesphere_soibam_test/'+sysId);
	userRequest.setHttpMethod('put');
	userRequest.setBasicAuth(SnowUser,SnowPassword);
	userRequest.setRequestHeader("Content-Type","application/json");
	vmRequestBody = '{"network_name":\"'+networkName+'\","ip_address":\"'+ipAddress+'\","state":\"'+status+'\"}';
	userRequest.setRequestBody(vmRequestBody);
	userGetResponse = userRequest.execute();
	gs.info(userGetResponse.getBody());
}



function wait(ms){
   var start = new Date().getTime();
     
   var end = start;
   while(end < start + ms) {
     end = new Date().getTime();
  }
}

