/*
 Function to update RITM description with request details
*/
function update_ritm(current_sys_id, datastore_name, esxi_hostname, volume_name)
{
	datastore_name = '\"'+datastore_name+'\"';
	esxi_hostname = '\"'+esxi_hostname+'\"';
	volume_name = '\"'+volume_name+'\"';
	
	var target = new GlideRecord('sc_req_item');
	target.addQuery('sys_id', current_sys_id);
	target.query(); // Issue the query to the database to get all records 
	while (target.next()) { 
		var desc_message = "Requested to create a LUN with name" + ' ' + volume_name + "on 3PAR Storage and assign it to" + ' ' + esxi_hostname + ' ' + "ESXI host and subsquently create" + ' ' + datastore_name + ' ' + "datastore on the exported volume.";
		target.description = desc_message;		
		target.update();
	}	
}

