function create_records_3par_table(ritm, job_id)
{
	var gr = new GlideRecord('x_228286_onesphere_3par');
	gr.initialize(); 
	gr.volume_name = volume_name; 
	gr.volume_unit = size_unit;
	gr.volume_size = size;
	gr.cpg = cpg;
	gr.storage_system_ip = storage_system_ip;
	gr.storage_cluster = storage_cluster;
	gr.vcenter_datastore = vcenter_datastore;
	gr.esxi_hostname = esxi_hostname;
	//ritm = '' + ritm + '';
	//job_id = '' + job_id + '';
	job_sys_id = get_job_sys_id(job_id);
	ritm_sys_id = get_ritm_sys_id(ritm);
	gr.ritm = ritm_sys_id,
	gr.job_id = job_sys_id,
	gr.insert();
}

