}

function initiate_lun_creation(auth_token)
{
	ansibleTower = auth_token[0] +'map/esxi-ds-create';				
	var request = new sn_ws.RESTMessageV2();
	//request.setEndpoint(ansibleTower+'/job_templates/37/launch/');				
	//request.setEndpoint(ansibleTower+'map/provision-3par');	
	request.setEndpoint(ansibleTower);
	request.setHttpMethod('POST');

	//Eg. UserName="admin", Password="admin" for this code sample.
	//var user = 'admin';
	//var password = 'gseteam';

	//request.setBasicAuth(user, password);
	request.setRequestHeader('Authorization', auth_token[1]);
	request.setRequestHeader("Accept", "application/json");
	request.setRequestHeader('Content-Type', 'application/json');	
	request.setMIDServer('t-system-new');
	request.setEccParameter('skip_sensor', true);
	
/*	
	var body = {};
    body["cpg"] = cpg,
//  body["datastore_name"] = datastore_name,
//  body["esxi_hostname"] = esxi_hostname,
//  body["hostset_host"] = hostset_host,
		
    body["volume_name"] = volume_name,
    body["volume_unit"] = volume_unit,
	body["volume_size"] = volume_size,
*/
	var body = {};
	body["extra_vars"] = {};
	body["extra_vars"]["volume_name"] = volume_name;
	body["extra_vars"]["size"] = size;
	body["extra_vars"]["size_unit"] = size_unit;
	body["extra_vars"]["cpg"] = cpg;
	body["extra_vars"]["storage_system_ip"] = storage_system_ip;
	body["extra_vars"]["storage_cluster"] = storage_cluster;
	body["extra_vars"]["vcenter_datastore"] = vcenter_datastore;
	body["extra_vars"]["esxi_hostname"] = esxi_hostname;
		
    request.setRequestBody(JSON.stringify(body));
// 	var response = request.execute();
	var response = request.executeAsync();
	response.waitForResponse(160);
	
	var request_response_body = response.getBody();
	var json_data = JSON.parse(request_response_body);
// 	gs.info(json.id);
// 	gs.info(json.summary_fields['project'].name);
// 	gs.info(json.summary_fields['job_template'].name);   
	create_records_job_table(json_data);
	
	return json_data.id;
}

function create_records_job_table(json_data)
{
	id = '' + json_data.id + '';
	name = '' + json_data.name + '';
	job_type = '' + json_data.job_type + '';
	status = '' + json_data.status + '';
	playbook = '' + json_data.playbook + '';
	project = '' + json_data.summary_fields['project'].name + '';
	job_template = '' + json_data.summary_fields['job_template'].name + '';
	
	var rec = new GlideRecord('x_228286_onesphere_ansible_awx') ;
	rec.initialize();
	rec.job_id = id;
	rec.job_name = name; 
	rec.job_type = job_type;
	rec.status = status;
	rec.playbook = playbook;
	rec.project = project;
	rec.job_template = job_template; 	
	rec.insert();
}

function autoplate_credentials()
{
   var target = {};
	target = new GlideRecord('x_228286_onesphere_environment_credentials');
	target.addQuery('number', 'Autoplat');
	target.query(); 
	while (target.next()) { 
		// code 
	}	
	return target;
}

function get_token_autoplate()
{
	autoplate_cred = autoplate_credentials();
    user = autoplate_cred.user;
    encry_password = autoplate_cred.password; 
    var password = encry_password.getDecryptedValue(); 

    url = autoplate_cred.onesphere_url;
	ansibleTower = url + 'login';   
	
    var request = new sn_ws.RESTMessageV2();
	request.setEndpoint(ansibleTower);	
	request.setHttpMethod('POST');
	
	request.setRequestHeader("Accept", "application/json");
	request.setRequestHeader('Content-Type', 'application/json');
	
	request.setMIDServer('t-system-new');
	request.setEccParameter('skip_sensor', true);

    var body = {};
    body["user"] = '' + user + '';
    body["password"] = '' + password + '';

	request.setRequestBody(JSON.stringify(body));
	var response = request.executeAsync();
	response.waitForResponse(160);	
	var request_response_body = response.getBody();        
	var json_data = JSON.parse(request_response_body);
	var token = [url, json_data.accessToken];
	//return json_data.accessToken;	
	return token;
}

