var oneSphereUri = "https://deic-gse04-hpe.hpeonesphere.com";
var oneSphereUser = "soibam.ana.singh@hpe.com";
var oneSpherePassword = "GSE#admin1";
var rollback = false;
var authToken = null;
var providerUri = null;
var networkUri = null;
var projectUri = null;
var selectedNetwork = current.variables.networks;
var selectedProject = current.variables.project;
var flag = 0;

process();
function process()
{
    try {
          authToken = getAuthToken();
          providerUri = findProviderUri(authToken);
          networkUri = findNetworkUri(authToken, providerUri);
		  projectUri = findProjectUri(authToken,selectedProject);
		  updateNetworkToProject(authToken,networkUri,projectUri);      
		  
	    } catch(x) {
	       gs.error("Process failed. Please check the logs");
           activity.result = "Failed";
	      }
		  activity.result = "Success";
		
		    if (authToken != null){
             deleteSession(authToken);
            }
		 
}



function getAuthToken(){
	try {
		oneSphereUser = '\"'+oneSphereUser+'\"';
        oneSpherePassword = '\"'+oneSpherePassword +'\"';
        var Session_request = new sn_ws.RESTMessageV2();
        Session_request.setHttpMethod('post');
        Session_request.setEndpoint(oneSphereUri+'/rest/session');
        Session_request.setRequestHeader("Content-Type","application/json");
        var body = '{"username":'+oneSphereUser+',"password":'+oneSpherePassword+'}';
        Session_request.setRequestBody(body);

        var Auth_response = Session_request.execute();
        var httpResponseStatus = Auth_response.getStatusCode();
        var responseBody = Auth_response.getBody();
        session = JSON.parse(responseBody);
	}
    catch (ex) {
		var message = Auth_response.getErrorMessage();
        gs.error("Error while acquring Auth Token: " +message);
	}
	return session.token;
}
function findProviderUri(authToken)
{
    try {	
	var providersUri = new sn_ws.RESTMessageV2();
    providersUri.setHttpMethod('get');
    providersUri.setEndpoint(oneSphereUri+'/rest/providers?query=providerTypeUri+EQ+/rest/provider-types/ncs&view=full');
    providersUri.setRequestHeader("Content-Type","application/json");
    providersUri.setRequestHeader("Authorization", authToken);
    var providersUriGetResponse = providersUri.execute();
    var providersUriResponseBody = providersUriGetResponse.getBody();
    var uri = JSON.parse(providersUriResponseBody);     
    //providerUri = uri.members[0].uri;
    gs.info("Private Cloud providersUri" + uri.members[0].uri);
		return uri.members[0].uri;
	}
	
	catch(ex) {
	   var message = providersUriGetResponse.getErrorMessage();
       gs.error("Error while finding Private Cloud Provider URI: " +message);
	}
	
}
function findNetworkUri(authToken, providerUri)
{
    try {
    //Get total zones availble in Private Cloud	
    var zonesList = new sn_ws.RESTMessageV2();
    zonesList.setEndpoint(oneSphereUri+'/rest/zones?query=providerUri+EQ+'+providerUri);
    zonesList.setHttpMethod('get');
    zonesList.setRequestHeader("Content-Type","application/json");
    zonesList.setRequestHeader("Authorization", authToken);
    var zonesListGetResponse = zonesList.execute();
    var zonesListResponseBody = zonesListGetResponse.getBody();
    var zones = JSON.parse(zonesListResponseBody);
	
	var key;
	for (key in zones.members) 
	{
	    var zoneUri = zones.members[key].uri;
		var regionsUriList = new sn_ws.RESTMessageV2();
		regionsUriList.setEndpoint(oneSphereUri+'/rest/networks?query=zoneUri+EQ+'+zoneUri);
		regionsUriList.setHttpMethod('get');
		regionsUriList.setRequestHeader("Content-Type","application/json");
		regionsUriList.setRequestHeader("Authorization", authToken);
		var regionsUriGetResponse = regionsUriList.execute();
		var regionsUriResponseBody = regionsUriGetResponse.getBody();
		var regionsUri = JSON.parse(regionsUriResponseBody);
		var key1;
		for (key1 in regionsUri.members)
		{
		         networkName = regionsUri.members[key1].name;
			     gs.info("Network" +networkName);
			 
			    if (networkName.toString() == selectedNetwork.toString())
                {
		     	             //gs.info("Equal");
                             networkId = regionsUri.members[key1].id;
                             gs.info("Network ID" +' ' +networkId);
                             flag = 1;
                             
							 /*
                             var add = '\"add\"';
                             var projectUris = '\"projectUris\"';
                             var value = '\"/rest/projects/6eeb86d644114da399306e3e503c025b\"';

                             var networksRequest = new sn_ws.RESTMessageV2();
                             networksRequest.setEndpoint(oneSphereUri+'/rest/networks/'+networkId);
                             networksRequest.setHttpMethod('patch');
                             networksRequest.setRequestHeader("Content-Type","application/json-patch+json");
		                     networksRequest.setRequestHeader("Authorization", authToken);
                             var body = '[{"op":'+add+',"path":'+projectUris+',"value":'+value+'}]';
                             networksRequest.setRequestBody(body);
                             var networksRequestGetResponse = networksRequest.execute();
                             var httpResponseStatus =   networksRequestGetResponse.getStatusCode();
                             var responseBody = networksRequestGetResponse.getBody();
                             gs.info(responseBody);
							 */
                                               
                                                

                }
                          if (flag == 1)
                         break;                    
        }
                if (flag == 1)
		break;
	}
	
	}
	catch(ex) {
	   var message = "Network URI not found";
       gs.error("Error while finding Network URI: " +message);
	}	
	return networkId;
}

function deleteSession(authToken) {
	try {
		oneSphereUser = '\"'+oneSphereUser+'\"';
        oneSpherePassword = '\"'+oneSpherePassword +'\"';
        var Session_request = new sn_ws.RESTMessageV2();
        Session_request.setHttpMethod('delete');
        Session_request.setEndpoint(oneSphereUri+'/rest/session');
        Session_request.setRequestHeader("Content-Type","application/json");
        Session_request.setRequestHeader("Authorization", String(authToken));

        var Auth_response = Session_request.execute();
        var httpResponseStatus = Auth_response.getStatusCode();
	}
	catch (ex) {
		var message = Auth_response.getErrorMessage();
        gs.error("Error while delete session: " +message);
	}
}

function findProjectUri(authToken,selectedProject)
{
    try {	
	var projectsUri = new sn_ws.RESTMessageV2();
    projectsUri.setHttpMethod('get');
    projectsUri.setEndpoint(oneSphereUri+'/rest/projects?userQuery=' +selectedProject);
    projectsUri.setRequestHeader("Content-Type","application/json");
    projectsUri.setRequestHeader("Authorization", authToken);
    var projectsUriGetResponse = projectsUri.execute();
    var projectsUriResponseBody = projectsUriGetResponse.getBody();
    var response = JSON.parse(projectsUriResponseBody);     
    //providerUri = response.members[0].uri;
    gs.info("Project URI" + response.members[0].uri);	
	return response.members[0].uri;
	} catch(ex) {
	   var message = projectsUriGetResponse.getErrorMessage();
       gs.error("Error while finding Private Cloud Provider URI: " +message);
	}
    
}
function updateNetworkToProject(authToken,networkUri,projectUri)
{
    try {
	     var add = '\"add\"';
         var projectUris = '\"projectUris\"';
         var value = '\"'+projectUri+'\"';
		 
         var networksRequest = new sn_ws.RESTMessageV2();
         networksRequest.setEndpoint(oneSphereUri+'/rest/networks/'+networkUri);
         networksRequest.setHttpMethod('patch');
         networksRequest.setRequestHeader("Content-Type","application/json-patch+json");
		 networksRequest.setRequestHeader("Authorization", authToken);
         var body = '[{"op":'+add+',"path":'+projectUris+',"value":'+value+'}]';
         networksRequest.setRequestBody(body);
         var networksRequestGetResponse = networksRequest.execute();
         var httpResponseStatus =   networksRequestGetResponse.getStatusCode();
         var responseBody = networksRequestGetResponse.getBody();
         gs.info(responseBody);
		 
	} catch(x) {
	  var message = networksRequestGetResponse.getErrorMessage();
      gs.error("Error while finding Private Cloud Provider URI: " +message);
	
	}

}

