function provision_esxi(url, token)
{
try {
		ansibleTower = url +'map/osda-osdeploy';				
		var request = new sn_ws.RESTMessageV2();
		//request.setEndpoint(ansibleTower+'/job_templates/37/launch/');				
		//request.setEndpoint(ansibleTower+'map/provision-3par');	
		request.setEndpoint(ansibleTower);
		request.setHttpMethod('POST');

		//Eg. UserName="admin", Password="admin" for this code sample.
		//var user = 'admin';
		//var password = 'gseteam';

		//request.setBasicAuth(user, password);
		request.setRequestHeader('Authorization', token);
		request.setRequestHeader("Accept", "application/json");
		request.setRequestHeader('Content-Type', 'application/json');	
		request.setMIDServer('t-system-new');
		request.setEccParameter('skip_sensor', true);
	
		
        var body = {};
		body["extra_vars"] = {};
		body["extra_vars"]["ilo_ip_address"] = ilo_ip_address;	
		body["extra_vars"]["os_name"] = os;
		body["extra_vars"]["mgmt_nic"] = nic;
		body["extra_vars"]["os_drive"] = drive;		
		//body["extra_vars"]["storage_system_ip"] = storage_system_ip;
		
       request.setRequestBody(JSON.stringify(body));
		// 	var response = request.execute();
		var response = request.executeAsync();
		response.waitForResponse(160);
	
		var request_response_body = response.getBody();
		var json_data = JSON.parse(request_response_body);
		return JSON.stringify(json_data);
		
    } catch (x) {
        gs.info("Failed to delete a LUN in 3PAR. Please look at logs for more details.");
    }
}

