function get_job_details()
{
	var r = new sn_ws.RESTMessageV2('x_228286_onesphere.Ansible Tower', 'Default GET');

//override authentication profile 
//authentication type ='basic'/ 'oauth2'
//r.setAuthenticationProfile(authentication type, profile name);

//set a MID server name if one wants to run the message on MID
//r.setMIDServer('MY_MID_SERVER');

//if the message is configured to communicate through ECC queue, either
//by setting a MID server or calling executeAsync, one needs to set skip_sensor
//to true. Otherwise, one may get an intermittent error that the response body is null
//r.setEccParameter('skip_sensor', true);

 var response = r.execute();
 var responseBody = response.getBody();
 var json_data = JSON.parse(responseBody);
 return JSON.stringify(json_data);
//   	return json_data;
 //var return_values = [json_data.id, json_data.name, json_data.job_type, json_data.status, json_data.playbook, json_data.summary_fields['project'].name,                       json_data.summary_fields['job_template'].name];
	//return json_data;
//return return_values;
	
}

function delete_volume(url, token) {
    try {
		ansibleTower = url +'map/delete-3par';				
		var request = new sn_ws.RESTMessageV2();
		//request.setEndpoint(ansibleTower+'/job_templates/37/launch/');				
		//request.setEndpoint(ansibleTower+'map/provision-3par');	
		request.setEndpoint(ansibleTower);
		request.setHttpMethod('POST');

		//Eg. UserName="admin", Password="admin" for this code sample.
		//var user = 'admin';
		//var password = 'gseteam';

		//request.setBasicAuth(user, password);
		request.setRequestHeader('Authorization', token);
		request.setRequestHeader("Accept", "application/json");
		request.setRequestHeader('Content-Type', 'application/json');	
		request.setMIDServer('t-system-new');
		request.setEccParameter('skip_sensor', true);
	
	
//       var body = {};
//     body["cpg"] = cpg,
//     body["datastore_name"] = datastore_name,
//     body["esxi_hostname"] = esxi_hostname,
//     body["hostset_host"] = hostset_host,
//       body["volume_name"] = volume_name,
//       body["volume_unit"] = volume_unit,
//       body["volume_size"] = volume_size,
		//var volume_name = current.variable_pool.volume_name;        
        //var storage_system_ip = current.variable_pool.storage_system_ip;


        
		
		

        var body = {};
		body["extra_vars"] = {};
		body["extra_vars"]["volume_name"] = volume_name;	
		body["extra_vars"]["storage_system_ip"] = storage_system_ip;
		
       request.setRequestBody(JSON.stringify(body));
// 	   var response = request.execute();
		var response = request.executeAsync();
	response.waitForResponse(160);
	
	var request_response_body = response.getBody();
	var json_data = JSON.parse(request_response_body);
	return JSON.stringify(json_data);
		
	//var return_values = [json_data.id, json_data.name, json_data.job_type, json_data.status, json_data.playbook, json_data.summary_fields['project'].name, json_data.summary_fields['job_template'].name];
	//return json_data;
		//return return_values;
    } catch (x) {
        gs.info("Failed to delete a LUN in 3PAR. Please look at logs for more details.");
    }
}

